CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

CREATE TABLE "Users" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "Password" text NOT NULL,
    "PasswordHint" text NOT NULL,
    "Email" text NOT NULL,
    CONSTRAINT "PK_Users" PRIMARY KEY ("Id")
);

CREATE TABLE "Vaults" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserId" integer NOT NULL,
    "Name" text NOT NULL,
    CONSTRAINT "PK_Vaults" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Vaults_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Categories" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "VaultId" integer NOT NULL,
    "Name" text NOT NULL,
    "Description" text,
    CONSTRAINT "PK_Categories" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Categories_Vaults_VaultId" FOREIGN KEY ("VaultId") REFERENCES "Vaults" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Subcategories" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "CategoryId" integer NOT NULL,
    "Name" text NOT NULL,
    "Url" text,
    "Notes" text,
    "Icon" text,
    CONSTRAINT "PK_Subcategories" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Subcategories_Categories_CategoryId" FOREIGN KEY ("CategoryId") REFERENCES "Categories" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Accounts" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "SubcategoryId" integer NOT NULL,
    "Login" text NOT NULL,
    "Email" text,
    "Password" text NOT NULL,
    "DateAdd" timestamp with time zone NOT NULL,
    "DateUpdate" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_Accounts" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Accounts_Subcategories_SubcategoryId" FOREIGN KEY ("SubcategoryId") REFERENCES "Subcategories" ("Id") ON DELETE CASCADE
);

CREATE INDEX "IX_Accounts_SubcategoryId" ON "Accounts" ("SubcategoryId");

CREATE INDEX "IX_Categories_VaultId" ON "Categories" ("VaultId");

CREATE INDEX "IX_Subcategories_CategoryId" ON "Subcategories" ("CategoryId");

CREATE INDEX "IX_Vaults_UserId" ON "Vaults" ("UserId");

